    dateIso: p.dateIso,
    coverSrc: p.coverSrc,
    coverAlt: p.coverAlt,
    categoryLabel: p.categoryLabel,
    topicSlug: p.topicSlug,
  };
}

function toSpotlightPosts(
  raw: Array<{
    slug: string;
    title: string;
    coverSrc?: string;
    coverAlt?: string;
    dateIso?: string;
  }>,
) {
  const posts: TagSpotlightPost[] = [...raw]
    .sort(byDateDesc)
    .map((p) => ({
      slug: p.slug,
      title: p.title,
      coverSrc: p.coverSrc,
      coverAlt: p.coverAlt,
    }));

  return posts;
}

