import {
  useReducer,
  useEffect,
  useLayoutEffect,
  useRef,
  useCallback,
} from 'react';

const TOP_RESET_Y = 64;
const DELTA_THRESHOLD = 12;

type Overlay = 'none' | 'drawer' | 'search';
type InputMode = 'pointer' | 'keyboard';

type ChromeState = {
  overlay: Overlay;
  inputMode: InputMode;
  headerHidden: boolean;
};

type ChromeAction =
  | { type: 'OPEN_DRAWER' }
  | { type: 'CLOSE_DRAWER' }
  | { type: 'TOGGLE_DRAWER' }
  | { type: 'OPEN_SEARCH' }
  | { type: 'CLOSE_SEARCH' }
  | { type: 'INPUT_KEYBOARD' }
  | { type: 'INPUT_POINTER' }
  | { type: 'HEADER_HIDE' }
  | { type: 'HEADER_SHOW' };

const initialState: ChromeState = {
  overlay: 'none',
  inputMode: 'pointer',
  headerHidden: false,
};

function enforceInvariants(state: ChromeState): ChromeState {
  if (state.overlay !== 'none' || state.inputMode === 'keyboard') {
    if (state.headerHidden) return { ...state, headerHidden: false };
  }
  return state;
}

function reducer(state: ChromeState, action: ChromeAction): ChromeState {
  let next = state;

  switch (action.type) {
    case 'OPEN_DRAWER': {
      next = { ...state, overlay: 'drawer', headerHidden: false };
      break;
    }

    case 'CLOSE_DRAWER': {
      if (state.overlay === 'drawer') {
        next = { ...state, overlay: 'none', headerHidden: false };
      }
      break;
    }

    case 'TOGGLE_DRAWER': {
      if (state.overlay === 'drawer') {
        next = { ...state, overlay: 'none', headerHidden: false };
      } else {
        next = { ...state, overlay: 'drawer', headerHidden: false };
      }
      break;
    }

    case 'OPEN_SEARCH': {
      next = { ...state, overlay: 'search', headerHidden: false };
      break;
    }

    case 'CLOSE_SEARCH': {
      if (state.overlay === 'search') {
        next = { ...state, overlay: 'none', headerHidden: false };
      }
      break;
    }

    case 'INPUT_KEYBOARD': {
      next = { ...state, inputMode: 'keyboard', headerHidden: false };
      break;
    }

    case 'INPUT_POINTER': {
      next = { ...state, inputMode: 'pointer' };
      break;
    }

    case 'HEADER_HIDE': {
      const isOverlayNone = state.overlay === 'none';
      const isPointerMode = state.inputMode === 'pointer';
      const autoHideAllowed = isOverlayNone && isPointerMode;

      if (autoHideAllowed && !state.headerHidden) {
        next = { ...state, headerHidden: true };
      }
      break;
    }

    case 'HEADER_SHOW': {
      if (state.headerHidden) next = { ...state, headerHidden: false };
      break;
    }

    default: {
      break;
    }
  }

  return enforceInvariants(next);
}

export function useChromeBehavior() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const stateRef = useRef<ChromeState>(state);
  useLayoutEffect(() => {
    stateRef.current = state;
  }, [state]);

  const lastScrollY = useRef(0);
  const scrollDelta = useRef(0);
  const lastDir = useRef<1 | -1 | 0>(0);

  const closeDrawer = useCallback(() => {
    dispatch({ type: 'CLOSE_DRAWER' });
  }, []);

  const closeSearch = useCallback(() => {
    dispatch({ type: 'CLOSE_SEARCH' });
  }, []);

  const openDrawer = useCallback(() => {
    dispatch({ type: 'OPEN_DRAWER' });
  }, []);

  const openSearch = useCallback(() => {
    dispatch({ type: 'OPEN_SEARCH' });
  }, []);

  const toggleDrawer = useCallback(() => {
    dispatch({ type: 'TOGGLE_DRAWER' });
  }, []);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Tab') {
        dispatch({ type: 'INPUT_KEYBOARD' });
      } else if ((e.ctrlKey || e.metaKey) && e.key.toLowerCase() === 'k') {
        e.preventDefault();
        dispatch({ type: 'OPEN_SEARCH' });
      } else if (e.key === 'Escape') {
        const s = stateRef.current;
        if (s.overlay === 'drawer') dispatch({ type: 'CLOSE_DRAWER' });
        if (s.overlay === 'search') dispatch({ type: 'CLOSE_SEARCH' });
      }
    };

    const handleMouseDown = () => {
      dispatch({ type: 'INPUT_POINTER' });
    };

    const handleTouchStart = () => {
      dispatch({ type: 'INPUT_POINTER' });
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('touchstart', handleTouchStart, { passive: true });

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('touchstart', handleTouchStart);
    };
  }, []);

  useEffect(() => {
    document.body.classList.toggle('is-scrollLocked', state.overlay !== 'none');
  }, [state.overlay]);

  useEffect(() => {
    const handleScroll = () => {
      const s = stateRef.current;
      const y = window.scrollY;

      if (y <= TOP_RESET_Y) {
        if (s.headerHidden) dispatch({ type: 'HEADER_SHOW' });
        scrollDelta.current = 0;
        lastDir.current = 0;
        lastScrollY.current = y;
        return;
      }

      const isOverlayNone = s.overlay === 'none';
      const isPointerMode = s.inputMode === 'pointer';
      const autoHideEnabled = isOverlayNone && isPointerMode;

      if (!autoHideEnabled) {
        scrollDelta.current = 0;
        lastDir.current = 0;
        lastScrollY.current = y;
        return;
      }

      const delta = y - lastScrollY.current;

      let dir: 1 | -1 | 0 = 0;
      if (delta > 0) dir = 1;
      else if (delta < 0) dir = -1;

      if (dir !== 0 && dir !== lastDir.current) {
        scrollDelta.current = delta;
        lastDir.current = dir;
      } else {
        scrollDelta.current += delta;
      }

      if (scrollDelta.current >= DELTA_THRESHOLD) {
        if (!s.headerHidden) dispatch({ type: 'HEADER_HIDE' });
        scrollDelta.current = 0;
      } else if (scrollDelta.current <= -DELTA_THRESHOLD) {
        if (s.headerHidden) dispatch({ type: 'HEADER_SHOW' });
        scrollDelta.current = 0;
      }

      lastScrollY.current = y;
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const drawerOpen = state.overlay === 'drawer';
  const searchOpen = state.overlay === 'search';

  return {
    drawerOpen,
    searchOpen,
    headerHidden: state.headerHidden,
    openDrawer,
    toggleDrawer,
    openSearch,
    closeDrawer,
    closeSearch,
  };
}

import { useEffect } from 'react';
import type { RefObject } from 'react';

export function useFocusTrap(
  containerRef: RefObject<HTMLElement | null>,
  isActive: boolean,
) {
  useEffect(() => {
    if (!isActive) {
      return () => undefined;
    }

    const container = containerRef.current;
    if (!container) {
      return () => undefined;
    }

    const focusableSelector = [
      'a[href]',
      'button:not([disabled])',
      'input:not([disabled])',
      'textarea:not([disabled])',
      'select:not([disabled])',
      '[tabindex]:not([tabindex="-1"])',
    ].join(', ');

    const getFocusable = () => (
      Array.from(
        container.querySelectorAll<HTMLElement>(focusableSelector),
      ).filter(
        (el) => !el.hasAttribute('disabled') && !el.getAttribute('aria-hidden'),
      )
    );

    const focusables = getFocusable();
    const first = focusables[0];

    if (first) first.focus();

    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Tab') return;

      const items = getFocusable();
      if (items.length === 0) return;

      const f = items[0];
      const l = items[items.length - 1];

      if (e.shiftKey) {
        if (document.activeElement === f) {
          e.preventDefault();
          l.focus();
        }
      } else if (document.activeElement === l) {
        e.preventDefault();
        f.focus();
      }
    };

    document.addEventListener('keydown', onKeyDown);

    return () => {
      document.removeEventListener('keydown', onKeyDown);
    };
  }, [isActive, containerRef]);
}

'use client';

import { useId, useMemo, useState } from 'react';
import Link from 'next/link';

import styles from './ContactCard.module.css';

type Status = 'idle' | 'sending' | 'success' | 'error';

function normalizeEmail(value: string): string {
  return value.trim().toLowerCase();
}

export function ContactCard() {
  const nameId = useId();
  const emailId = useId();
  const messageId = useId();
  const consentId = useId();
  const websiteId = useId();

  const [status, setStatus] = useState<Status>('idle');
  const [errorText, setErrorText] = useState<string>('');

  const [name, setName] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [message, setMessage] = useState<string>('');
  const [consent, setConsent] = useState<boolean>(false);

  const canSubmit = useMemo(() => {
    if (!name.trim()) return false;
    if (!normalizeEmail(email)) return false;
    if (!message.trim()) return false;
    if (!consent) return false;
    return status !== 'sending';
  }, [name, email, message, consent, status]);

  async function onSubmit(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault();

    setStatus('sending');
    setErrorText('');

    const payload = {
      name: name.trim(),
      email: normalizeEmail(email),
      message: message.trim(),
      consent,
      website: (e.currentTarget.elements.namedItem('website') as HTMLInputElement | null)?.value,
      pageUrl: typeof window !== 'undefined' ? window.location.href : '',
      userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : '',
    };

    try {
      const res = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const data = (await res.json().catch(() => null)) as { error?: string } | null;
        throw new Error(data?.error || 'Request failed.');
      }

      setStatus('success');
      setName('');
      setEmail('');
      setMessage('');
      setConsent(false);
    } catch (err) {
      setStatus('error');
      setErrorText(err instanceof Error ? err.message : 'Something went wrong.');
    }
  }

  return (
    <section className={styles.root} aria-label="Contact">
      <div className={styles.card}>
        <div className={styles.lead}>
          <div className={styles.badge} aria-hidden="true">
            <svg viewBox="0 0 24 24" className={styles['badge-icon']}>
              <path
                d="M4 6h16v12H4V6zm0 0l8 7 8-7"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinejoin="round"
                strokeLinecap="round"
              />
            </svg>
          </div>

          <div className={styles['lead-text']}>
            <h2 className={styles.title}>Contact</h2>
            <p className={styles.subtitle}>
              Send a message and well get back to you by email.
            </p>

            <p className={styles.meta}>
              Or email directly:{' '}
              <a className={styles['meta-link']} href="mailto:gulemeyenosgeguler@gmail.com">
                gulemeyenosgeguler@gmail.com
              </a>
            </p>
          </div>
        </div>

        <div className={styles.formcol}>
          {status === 'success' ? (
            <div className={styles.notice} role="status" aria-live="polite">
              <p className={styles['notice-title']}>Message sent.</p>
              <p className={styles['notice-text']}>Thanks  well reply by email.</p>

              <button
                type="button"
                className={styles['notice-button']}
                onClick={() => setStatus('idle')}
              >
                Send another
              </button>
            </div>
          ) : (
            <form className={styles.form} onSubmit={onSubmit}>
              <div className={styles.honeypot}>
                <label className={styles.label} htmlFor={websiteId}>
                  Website
                </label>
                <input id={websiteId} name="website" className={styles.input} type="text" />
              </div>

              <div className={styles.field}>
                <label className={styles.label} htmlFor={nameId}>
                  Name
                </label>
                <input
                  id={nameId}
                  className={styles.input}
                  type="text"
                  autoComplete="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              <div className={styles.field}>
                <label className={styles.label} htmlFor={emailId}>
                  Email
                </label>
                <input
                  id={emailId}
                  className={styles.input}
                  type="email"
                  autoComplete="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>

              <div className={styles.field}>
                <label className={styles.label} htmlFor={messageId}>
                  Message
                </label>
                <textarea
                  id={messageId}
                  className={styles.textarea}
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  required
                  rows={6}
                />
              </div>

              <div className={styles['consent-row']}>
                <input
                  id={consentId}
                  className={styles.checkbox}
                  type="checkbox"
                  checked={consent}
                  onChange={(e) => setConsent(e.target.checked)}
                  required
                />

                <label className={styles['consent-text']} htmlFor={consentId}>
                  By submitting, you agree to our{' '}
                  <Link className={styles['consent-link']} href="/pages/terms">
                    Terms
                  </Link>{' '}
                  and{' '}
                  <Link className={styles['consent-link']} href="/pages/privacy">
                    Privacy Policy
                  </Link>
                  .
                </label>
              </div>

              {status === 'error' ? (
                <p className={styles.error} role="alert">
                  {errorText}
                </p>
              ) : null}

              <button className={styles.button} type="submit" disabled={!canSubmit}>
                {status === 'sending' ? 'SENDING' : 'SEND MESSAGE'}
              </button>
            </form>
          )}
        </div>
      </div>
    </section>
  );
}

